          <div className={`${styles["my-masnry-user-prof-desc"]} flex flex-column`}>
            <h1>{editedName}</h1>
            <h3>{editedJob}</h3>

          </div>
        </div>
        <img src={Delete} alt="" className={styles["download"]} />
        <a href={Links}>
          <img src={Download} alt="" className={styles["download"]} />
        </a>
      </div>
    </div>
  );
}

