// import styles of this component
import styles from "./MasonryLayout.module.css"

// import other react pkg to use
import Masonry from "react-masonry-css"

// import other component to use
import MasonryBox from './MasonryBox/MasonryBox';

// MasonryLayout Component
const MasonryLayout = ({ images }) => {
  const breakpointColumnsObj = {
    default: 4,
    1100: 3,
    700: 2,
    500: 1
  };
}
const ImageGallery = () => {
  const [images, setImages] = useState([]);

  useEffect(() => {
    async function fetchImages() {
      try {
        const response = await axios.get('http://localhost:5000/api/images');
        setImages(response.data);
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    }
    fetchImages();
  }, []);


  const handleDelete = async (imageId, imageKitId) => {
    try {
      imagekit.deleteFile(imageKitId, function (error, result) {
        if (error) {
          console.log(error);
        } else {
          console.log(result);
          axios.delete(`http://localhost:5000/api/images/${imageId}`);
          setImages(images.filter(image => image.imageKitId !== imageKitId));
        }
      });
    } catch (error) {
      console.error('Error deleting image:', error);
    }
  };




  return (
    <Masonry
      breakpointCols={breakpointColumnsObj}
      className={styles["my-masonry-grid"]}
      columnClassName={styles["my-masonry-grid_column"]}
    >
      {images.map(item => (
        <MasonryBox 
          key={item.id} 
          wallSrc={item.src} 
          userProf={item.user.src} 
          userName={item.user.name} 
          userJob={item.jobPost} 
          Links={item.user.link}
        />
      ))}
    </Masonry>
  )
      }

export default MasonryLayout
