import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './gallery.css';

const ImageGallery = () => {
  const [images, setImages] = useState([]);

  useEffect(() => {
    async function fetchImages() {
      try {
        const response = await axios.get('http://localhost:5000/api/images');
        setImages(response.data);
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    }
    fetchImages();
  }, []);


  const handleDelete = async (imageId, imageKitId) => {
    try {
      imagekit.deleteFile(imageKitId, function (error, result) {
        if (error) {
          console.log(error);
        } else {
          console.log(result);
          axios.delete(`http://localhost:5000/api/images/${imageId}`);
          setImages(images.filter(image => image.imageKitId !== imageKitId));
        }
      });
    } catch (error) {
      console.error('Error deleting image:', error);
    }
  };

  return (
    <div className="image-gallery">
      {images.map((image) => (
        <div key={image.id} className="image-card">
          <img src={image.imagePath} alt={`Image ${image.id}`} className="image" />
          <div className="image-details">
            <h2>{image.name}</h2>
            <p>{image.jobPost}</p>
            <a href={image.imagePath} download className="download-button">
              Download
            </a>
          </div>
          <button onClick={() => handleDelete(image._id, image.imageKitId)}>
              Delete
            </button>
        </div>
      ))}
    </div>
  );
};

export default ImageGallery;
