import React, { useState,useEffect } from 'react';
import axios from 'axios'; // Import Axios library
import styles from "./MasonryBox.module.css"
import { PropTypes } from 'prop-types';
import Download from "./Download.png";
import Delete from './Delete.png';
import Edit from "./Edit.png";
import "./mybutton.css";


const MasonryBox = ({ wallSrc, userName, userJob, Links }) => {
  const [editing, setEditing] = useState(false);
  const [editedName, setEditedName] = useState(userName);
  const [editedJob, setEditedJob] = useState(userJob);

  const [editingImage, setEditingImage] = useState(null);
  const [newName, setNewName] = useState('');
  const [newJobPost, setNewJobPost] = useState('');

  const fetchImages = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/images');
      setImages(response.data);
    } catch (error) {
      console.error('Error fetching images:', error);
    }
  };

  const handleDelete = async (imageName) => {
    try {
      const response = await axios.post('http://localhost:5000/api/delete', { imageName });
      console.log('Image deleted:', response.data);
      fetchImages();
    } catch (error) {
      console.error('Error deleting image:', error);
    }
  };
  const enableEditing = (imageName) => {
    setEditingImage(imageName);
    const image = images.find((img) => img.imageName === imageName);
    setNewName(image.name);
    setNewJobPost(image.jobPost);
  };

  const handleUpdate = async (imageName) => {
    try {
      const response = await axios.post('http://localhost:5000/api/update', {
        imageName,
        newName,
        newJobPost,
      });
      console.log('Image updated:', response.data);
      setEditingImage(null);
      setNewName('');
      setNewJobPost('');
      fetchImages();
    } catch (error) {
      console.error('Error updating image:', error);
    }
  };


  useEffect(() => {
    fetchImages();
  }, []);


  return (
    <div className={styles["my-masonry"]}>
      <img src={wallSrc} style={{ width: "100%" }} alt="" />
      <div className={`${styles["my-masnry-description"]} flex`}>
        <div className={`${styles["my-masnry-user-box"]} flex align-items-center`}>
          <div className={styles["my-masnry-user-prof"]}>
            {editing ? (
              <>

            <div className='button2'>
                <input className='myinput'
                  type="text"
                  value={editedName}
                  onChange={e => setEditedName(e.target.value)}
                  placeholder="Image Creator Name"
                />
                <input
                  type="text" className='myinput'
                  value={editedJob}
                  onChange={e => setEditedJob(e.target.value)}
                  placeholder="Image Creator Job"
                />
            <div className="mybutton2">
                <button className="mybutton"onClick={handleUpdate}>Save</button>
                <button className="mybutton"onClick={handleDelete}>Cancel</button>
                </div>
                </div>

              </>
            ) : (
              <img
                src={Edit}
                alt=""
                onClick={enableEditing}
                className={styles["edit"]}
              />
            )}
          </div>

          <div className={`${styles["my-masnry-user-prof-desc"]} flex flex-column`}>
            <h1>{editedName}</h1>
            <h3>{editedJob}</h3>

          </div>
        </div>
        <img src={Delete} alt="" className={styles["download"]} />
        <a href={Links}>
          <img src={Download} alt="" className={styles["download"]} />
        </a>     


      </div>
    </div>
  );
}

MasonryBox.propTypes = {
  wallSrc: PropTypes.string.isRequired,
  userName: PropTypes.string.isRequired,
  userJob: PropTypes.string.isRequired,
  Links: PropTypes.string.isRequired,
}

export default MasonryBox;
