import React, { useState } from 'react';
import axios from 'axios';
import './ImageUploadForm.css'; // Your custom CSS file

const ImageUploadForm = () => {
  const [name, setName] = useState('');
  const [jobPost, setJobPost] = useState('');
  const [image, setImage] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleJobPostChange = (e) => {
    setJobPost(e.target.value);
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append('name', name);
    formData.append('jobPost', jobPost);
    formData.append('image', image);

    try {
      const response = await axios.post('http://localhost:5000/api/upload', formData); 
      console.log('Upload successful:', response.data);
      // Handle success or further actions here
    } catch (error) {
      console.error('Error uploading:', error);
      // Handle error here
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="image-upload-form">
      <h2>Image Upload Form</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" value={name} onChange={handleNameChange} />
        </div>
        <div className="form-group">
          <label htmlFor="jobPost">Job Post:</label>
          <input type="text" id="jobPost" value={jobPost} onChange={handleJobPostChange} />
        </div>
        <div className="form-group">
          <label htmlFor="image">Image:</label>
          <input type="file" id="image" accept="image/*" onChange={handleImageChange} />
        </div>
        <button type="submit" disabled={loading}>Submit</button>
      </form>
    </div>
  );
};

export default ImageUploadForm;
